INSERT INTO creature_template (`entry`, `modelid1`, `name`, `subname`, `IconName`, `gossip_menu_id`, `minlevel`, `maxlevel`, `faction`, `npcflag`, `speed_walk`, `speed_run`, `scale`, `rank`, `unit_class`, `unit_flags`, `type`, `type_flags`, `RegenHealth`, `flags_extra`, `AiName`, `ScriptName`) VALUES
(@Entry, @Model, @Name, @Title, @Icon, @GossipMenu, @MinLevel, @MaxLevel, @Faction, @NPCFlag, 1, 1.14286, @Scale, @Rank, 1, 2, @Type, @TypeFlags, 1, @FlagsExtra, @AIName, @Script);

-- NPC Text
SET @text_id 		:= 60001;
DELETE FROM `npc_text` WHERE `ID`>=@text_id and ID<=@text_id+6;
INSERT INTO `npc_text` (`ID`, `text0_0`) VALUES (@text_id, '凡人 $N. 如果你有泰坦符文或泰坦精华，就可以在我这里挖掘你的天命！当你拥有附着泰坦能量的装备的时候在来找我！');
INSERT INTO `npc_text` (`ID`, `text0_0`) VALUES (@text_id+1, '你的背包中没有附魔所需的 泰坦符文 ！');
INSERT INTO `npc_text` (`ID`, `text0_0`) VALUES (@text_id+2, '你的背包中没有附魔所需的 泰坦精华 ！');
INSERT INTO `npc_text` (`ID`, `text0_0`) VALUES (@text_id+3, '你的背包中没有泰坦装备！');
INSERT INTO `npc_text` (`ID`, `text0_0`) VALUES (@text_id+4, '消耗泰坦符文来给你的装备重新附魔！');
INSERT INTO `npc_text` (`ID`, `text0_0`) VALUES (@text_id+5, '消耗泰坦精华来提高你的附魔属性！');
INSERT INTO `npc_text` (`ID`, `text0_0`) VALUES (@text_id+6, '|ca335ee00-=泰坦附魔=-|r$B$B所有80级紫色项链、饰品都有几率出现泰坦附魔额外属性。$B泰坦符文：可用于给已有附魔效果的装备重新附魔，随机出现新的附魔效果。$B泰坦精华：可用于给已有附魔属性的装备强化附魔数值。$B$B属性系列：$B力量、敏捷、智力：16-50$B精神：18-55$B暴击、急速、护甲穿透：25-76$B攻击强度：26-94$B法术强度：26-62$B招架、躲闪：25-81$B护甲：225-700');

